    /**
     * checks if all numbers from 0 to 8 is present and only appears once
     * @return  true if all numbers are present and no duplicates, else false
     */
	private boolean allPresentCheck(){
	    int x1 = 0;
        int x2 = 0;
        int x3 = 0;
        int x4 = 0;
        int x5 = 0;
        int x6 = 0;
        int x7 = 0;
        int x8 = 0;
        int x9 = 0;
	    for(int x = 0; x < 3; x++){
	        for(int y = 0; y < 3; y++){
	            if(board[x][y] == 0){
	                x1++;
                }

                else if(board[x][y] == 1){
	                x2++;
                }
                else if(board[x][y] == 2){
                    x3++;
                }
                else if(board[x][y] == 3){
                    x4++;
                }
                else if(board[x][y] == 4){
                    x5++;
                }
                else if(board[x][y] == 5){
                    x6++;
                }
                else if(board[x][y] == 6){
                    x7++;
                }
                else if(board[x][y] == 7){
                    x8++;
                }
                else if(board[x][y] == 8){
                    x9++;
                }
                else if(board[x][y] == ' '){
	                return false;
                }
            }
        }

	    if(x1 + x2 + x3 + x4 + x5 + x6 + x7 +x8 + x9 == 9){
	        return true;
	    }
	    else return false;
    }
